mapper.select:
select b.bno, b.title, b.content, b.userid, b.regdate, b.replycnt, b.viewcnt
from(
	select rownum rn, a.* from(
		select * from Moram_Lecture_Board
		order by bno desc
	) a
	
) b
where rn between #{start} and #{end}

	
		select d.bno, d.title, d.content, d.regdate, d.replycnt, d.viewcnt, d.name 
	from (
 	 select rownum rn, c.*
  		from (
   			 select a.*, b.* from moram_lecture_board a, moram_member b
    		 where a.userid = b.id
   			 order by bno desc
 	 	) c
 	
	)d
	where rn between #{start} and #{end}

   
